mapper.select:

select *
from
(
    select rownum as RNUM, list.*
    from (
        select pfno, sname ,cgno, cgname, pftitle, filename	
		 from PFLISTVIEW
		 where
		 
		 
		ROWID IN (SELECT MAX(ROWID) FROM pflistview GROUP BY pfno)
		order by pfno desc
    )list
)

where RNUM>#{firstRecordIndex} 
and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}
