mapper.select:
select *
from
(
    select rownum as RNUM, list.*
    from (
        select 	
		from rqlist
		where
		 
		
		ROWID IN (SELECT MAX(ROWID) FROM rqlist GROUP BY rqno)
		order by rqno desc
    )list
)

where RNUM>#{firstRecordIndex} 
and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

mapper.update:
update request
set pickcount=pickcount+1
where rqno=#{no}

update pickall
set plevel = plevel+1
where pickno=#{no}

update pickall
set sprice=#{price}
where pickno=#{no}

update member
set mileage=#{mileage}
where memberid=#{memberId}

   		update request
		set delflag='Y'
		where rqno=#{no}

   		update request
		set delflag='N'
		where rqno=#{no}

update pick
set plevel=plevel+1
where plevel=2 and pickno=#{pickno}

mapper.select:
select *
from
(
    select rownum as RNUM, list.*
    from (
        select 	
		from rqlist
		where
		
			cgno = #{searchVo.searchCondition} and
		
		
			rqdetail like '%' || #{searchVo.searchKeyword} || '%' and
		
		memberId=#{memberId}
		order by rqno desc
    )list
)

where RNUM>#{searchVo.firstRecordIndex} 
and RNUM<=#{searchVo.firstRecordIndex}+#{searchVo.recordCountPerPage}

mapper.update:
update pickall
set sprice=#{sprice}
where rqno=#{no} and plevel in (select max(plevel) from pickall where rqno=#{no})
