mapper.select:
select *
from
(
    select rownum as RNUM, list.*
    from (
        select 
		
		from QNA
		order by qno desc
    )list
)    

where RNUM>#{firstRecordIndex} 
and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

mapper.update:
update qna
set qtitle=#{qTitle}, qcontent=#{qContent}
where qno=#{qNo}

update qnare
set content = #{content}
where qrno = #{qrNo} and qno = #{qNo}
